import React from 'react';

import {Link, withRouter} from 'react-router-dom';

import './status-filter.scss';

const StatusFilter =({location})=>{
    const currentPath=location.pathname;
    const classNames="filter-bar-all btn col-sm"
    return (
      <div className="filter-bar row">
        <Link   to='/'
        className={currentPath.search('/category') ? classNames:classNames+' active'}
                className="filter-bar-all btn col-sm">All</Link>
        <Link   to='/active'
                className="filter-bar-active btn col-sm">Active</Link>
        <Link   to='/done'
                className="filter-bar-done btn col-sm">Done</Link>
      </div>
    );
}

export default StatusFilter;
