import React from 'react';
import ReactDOM from 'react-dom';
import {Provider} from 'react-redux';
import { PersistGate } from 'redux-persist/integration/react';
import { createStore } from 'redux';

import todos from './data/reducers/todos';
//import configureStore from './data/configureStore';
import { persistStore, persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage';


import App from './components/app';


//const store = configureStore();

const initialState = {todos:[
      {id:'0',label:'sdlsjh',done:false},
      {id:'1',label:'dhj',done:false},
      {id:'2',label:'fsxj',done:false}

]};


const persistConfig = {
    key: 'root',
    storage,
};

const persistedReducer = persistReducer(persistConfig, todos);

export const store = createStore(persistedReducer, initialState);
export const persistor = persistStore(store);

ReactDOM.render(
  <Provider store={store}>
        <PersistGate loading={null} persistor={persistor}>
              < App / >
        </PersistGate>
  </Provider>,
document.getElementById('root'));
